      current: '',
      previous: []
    }
  }

  reset = () => {
    this.setState({result:0})
  }

  render() {
    const buttons = [
      {symbol: 'C', cols: 0, action: this.reset}
    ];
    return (
      <div className="App">
        <input className="result" type="text" value={this.state.current} />



