import React, {Component} from 'react';
import Button from './Components/Button';
import "./css/style.css"

class App extends Component 
{
  constructor(props)
  {
    super(props);

    this.state = {
      current: '',
      previous: [],
      nextIsReset: false
    }
  }

  reset = () => {
    this.setState({current: '0', previous:[]});
  }

  addToCurrent = (symbol) =>
  {
    if(["/","-", "X","+"].indexOf(symbol) > -1 )
    {
      let {previous} = this.state;
      previous.push(this.state.current + symbol);
      this.setState({previous, nextIsReset: true}); // bc of the same name

    }
    else
    {
      if ((this.state.current === "0" && symbol !== ".")|| this.state.nextIsReset)
      {
        this.setState({current:symbol, nextIsReset:false});
      }
      else
      {
        this.setState({current:this.state.current + symbol});
      }

    }
  }

  calculate = () =>
  {
    this.setState({current: (this.previous + this.state.current), previous: []});

  }

  render() {
    const buttons = [
      {symbol: 'C', cols: 3, action: this.reset},
      {symbol: '/', cols: 1, action: this.addToCurrent},
      {symbol: '7', cols: 1, action: this.addToCurrent},
      {symbol: '8', cols: 1, action: this.addToCurrent},
      {symbol: '9', cols: 1, action: this.addToCurrent},
      {symbol: 'X', cols: 1, action: this.addToCurrent},
      {symbol: '4', cols: 1, action: this.addToCurrent},
      {symbol: '5', cols: 1, action: this.addToCurrent},
      {symbol: '6', cols: 1, action: this.addToCurrent},
      {symbol: '-', cols: 1, action: this.addToCurrent},
      {symbol: '1', cols: 1, action: this.addToCurrent},
      {symbol: '2', cols: 1, action: this.addToCurrent},
      {symbol: '3', cols: 1, action: this.addToCurrent},
      {symbol: '+', cols: 1, action: this.addToCurrent},
      {symbol: '0', cols: 2, action: this.addToCurrent},
      {symbol: '.', cols: 1, action: this.addToCurrent},
      {symbol: '=', cols: 1, action: this.calculate},
    ];
    return (
      <div className="App">
        {this.state.previous.length > 0 ? 
      <div className="float-last">{this.state.previous[this.state.previous.length-1]}</div> : null
      }
        <input className="result" type="text" value={this.state.current} />
        {
          buttons.map((btn, i) => {
            return <Button key={i} symbol = {btn.symbol} cols={btn.cols} action={(symbol)=> btn.action(symbol)} />
          })
        }


      </div>
    );
  }
}

export default App;
